import { useEffect, useMemo, useRef, useState } from 'react';

export default function createHook(useHook, ...initialArgs) {
  const hook = new Hookleton(useHook, initialArgs);
  const useFn = hook.useFn.bind(hook);
  useFn.get = hook._get.bind(hook);
  return useFn;
}

export class Hookleton {
  constructor(useHook, initialArgs) {
    this.useHook = useHook;
    this.args = initialArgs;

    this.hookOut = [];
    this._updaters = new Map();
  }

  useFn(...initial) {
    // First hook call become the 'hookleton'
    const isFirst = useRef(false);
    useMemo(() => this._onInit(isFirst), []); // only one `useFn` instance will be true

    if (isFirst.current) {
      // Set shared `hook` output
      this.hookOut = this.useHook(...this.args);

      // Checked at start on 'first' render
      useMemo(() => {
        if (!Array.isArray(this.hookOut)) {
          throw new Error("[Hookleton] provided 'useHook' must return array values");
        }
      }, []);

      // prevent Exception on 'hookleton' unmount
      // by **CONVENTION** updater is `hookOut[1]
      useEffect(() => () => (this.hookOut[1] = this._unmountedFirst), []);

      // Lifecycle hook: `on render first`
      return this.useOnFirst(initial);
    }

    // Lifecycle hook: `on render first`
    return this.useNotFist(initial);
  }

  // Here we use a custom function `updateSlaves` that ignore first render call.
  useOnFirst() {
    useEffect(this.__updateSlaves.bind(this), [this.hookOut[0]]);
    return this.hookOut;
  }

  useOnSlaveRender() {
    // Get slave 'updater' for, later, force render it.
    // Slave state is ignore because we have only one state set in 'master
    const [, updater] = useState();

    useEffect(() => {
      // a Map of key/value: updater/updater is useful for some kinds of hooks composed on top of `useFn`
      this.slaves.set(updater, wrapUpdater(updater));
      return () => this.slaves.delete(updater); // on unmount
    }, []);

    return this.hookOut;
  }

  _onInit(isMaster) {
    this._onInit = () => {};
    isMaster.current = true;
  }

  _updateSlaves() {
    // State, `hookOut[0]`, is passed to slaves updater. This value is not use here,
    // but could be useful for libs that extends Hookleton
    this.slaves.forEach(updater => updater(this.hookOut[0]));
  }

  __updateSlaves() {
    // skip first(unexpected) callback call
    this.__updateSlaves = this._updateSlaves;
  }

  _get() {
    return this.hookOut;
  }

  // Prevent a Exception get throwed when someone is trying to update a unmounted 'master',
  // behaviour can be changed with `unmountedSingleton` config setting
  _unmountedFirst() {
    console.warn("[Hookleton] Singleton it's unmounted");
  }
}

// slaves are updated negating '~' his current state. This force to rerender it
const wrapUpdater = updater => () => updater(forceUpdate);
const forceUpdate = s => ~s;
