import { useEffect, useMemo, useRef, useState } from 'react';

export default function createHook(useHook, ...initialArgs) {
  const hook = new HookletonCore(useHook, initialArgs);
  const useFn = hook.useFn.bind(hook);
  useFn.get = hook._get.bind(hook);
  return useFn;
}

export class Hookleton extends HookletonCore {
  constructor(useHook, initialArgs) {
    super(useHook, initialArgs);
  }

  useOnRenderFirst(initial) {
    return super.useOnRenderFirst(initial).concat({ isFirst: true });
  }

  useOnSlaveRender(initial) {
    return super.useOnSlaveRender(initial).concat({ isFirst: false });
  }
}

export class HookletonCore {
  constructor(useHook, initialArgs) {
    this.useHook = useHook;
    this.args = initialArgs;

    this.hookOut = [];
    this._updaters = new Map();
  }

  useFn(...initial) {
    // First hook call become the 'hookleton'
    const isFirst = useRef(false);
    useMemo(() => this._onInit(isFirst), []); // only one `useFn` instance will be true

    if (isFirst.current) {
      // Set shared `hook` output
      this.hookOut = this.useHook(...this.args);

      // Checked at start on 'first' render
      useMemo(() => {
        if (!Array.isArray(this.hookOut)) {
          throw new Error("[Hookleton] provided 'useHook' must return array values");
        }
      }, []);

      // prevent Exception on 'hookleton' unmount
      // by **CONVENTION** updater is `hookOut[1]
      useEffect(() => () => (this.hookOut[1] = this._unmountedFirst), []);

      // Lifecycle hook: `on render first`
      return this.useOnRenderFirst(initial);
    }

    // Lifecycle hook: `on render the rest`
    return this.useOnRenderRest(initial);
  }

  // Here we use a custom function that ignore first render call.
  useOnRenderFirst() {
    useEffect(this.__updateRest.bind(this), [this.hookOut[0]]);
    return this.hookOut;
  }

  useOnRenderRest() {
    // Get 'updater' for, later, force render it.
    // state is ignore because we have only one state set in 'hookleton'
    const [, updater] = useState();

    useEffect(() => {
      // a Map of key/value: updater/updater is useful for some kinds of hooks
      // composed on top of `useFn`
      this._updaters.set(updater, wrapUpdater(updater));
      return () => this._updaters.delete(updater); // on unmount
    }, []);

    return this.hookOut;
  }

  _onInit(ref) {
    this._onInit = () => {};
    ref.current = true;
  }

  _updateRest() {
    // State, `hookOut[0]`, is passed to updater. This value is not use here,
    // but could be useful for libs that extends Hookleton
    this._updaters.forEach(updater => updater(this.hookOut[0]));
  }

  __updateRest() {
    // skip first(unexpected) callback call
    this.__updateRest = this._updateRest;
  }

  _get() {
    return this.hookOut;
  }

  // Prevent a Exception get throwed when someone is trying to update a unmounted 'hookleton',
  _unmountedFirst() {
    console.warn("[Hookleton] 'first' was unmounted");
  }
}

// Update negating '~' his current state. This force to rerender it
const wrapUpdater = updater => () => updater(forceUpdate);
const forceUpdate = s => ~s;
