import { useState, useEffect, useRef, useMemo } from 'react';
import { HookletonCore } from './index';

export default function createHook(useHook, ...initialArgs) {
  return createHook2(PredicateCore, useHook, ...initialArgs);
}

export function createHook2(predicateClass, useHook, ...initialArgs) {
  const hook = new predicateClass(useHook, initialArgs);
  const useFn = hook.useFn.bind(hook);
  useFn.get = hook._get.bind(hook);
  return useFn;
}

export class PredicateCore extends HookletonCore {
  useOnRenderRest([arg1]) {
    let predicate;
    const hasSet = useRef(false);
    useMemo(() => {
      if (Array.isArray(arg1)) {
        hasSet.current = true;
        predicate = arg1[0];
      } else {
        predicate = arg1;
      }
    }, []);

    const [, updater] = useState();

    useEffect(() => {
      this._saveUpdater(updater, predicate);
      return () => this._updaters.delete(updater); // on unmount
    }, []);

    return hasSet.current
      ? this._hookOut.concat({ setPredicate: this._setPredicate(updater) })
      : this._hookOut;
  }

  _saveUpdater(updater, predicate) {
    this._updaters.set(updater, withPredicate(updater, predicate));
  }

  _setPredicate(updater) {
    return predicate => {
      this._saveUpdater(updater, predicate);
      this._updaters.get(updater)(this._hookOut[0]);
    };
  }
}

export class Predicate extends PredicateCore {
  useOnRenderFirst(initial) {
    return super.useOnRenderFirst(initial).concat({ isFirst: true, setPredicate });
  }

  useOnRenderRest(initial) {
    let hookOut = super.useOnRenderRest(initial);
    const len = hookOut.length;
    if (len === this.hookOut.length) {
      hookOut = hookOut.concat({ isFirst: false, setPredicate });
    } else {
      hookOut[len - 1].isFirst = false;
    }
    return hookOut;
  }
}

const setPredicate = () => {};

// Add 'predicate' function to updater
function withPredicate(updater, p) {
  const predicate = normalize(p);
  return s => predicate(s) && updater(forceUpdate);
}

const forceUpdate = s => ~s;

function normalize(p) {
  switch (typeof p) {
    case 'undefined':
      return TRUEFN;
    case 'function':
      return p;
    case 'boolean':
      return () => p;
    default:
      console.warn("[Hookleton] predicate '" + p + "' is not boolean or function");
      return FALSEFN;
  }
}

const TRUEFN = () => true;
const FALSEFN = () => false;
