import { useState, useEffect } from 'react';
import createHok from '../component';

// custom mouse position hook
const onMousePosition = createHok(() => {
  let [position, setPosition] = useState({ x: null, y: null });

  const handleMouseMove = e => setPosition({ x: e.pageX, y: e.pageY });

  useEffect(() => {
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  return [position];
});


function Mouse() {
  const [mousePosition] = onMousePosition();
  return (
    <div>
      <div>
        x: {mousePosition.x}, y: {mousePosition.y}
      </div>
    </div>
  );
}

export default () => (
  <>
    <table>
      <tbody>
        <tr>
          {Array.from({ length: 10 }).map((_, i) => (
            <td key={i}>
              <Mouse />
            </td>
          ))}
        </tr>
      </tbody>
    </table>
