import { useState, useEffect, useCallback } from 'react';
import createHook from '../component';

const throttle = (func, limit) => {
  let inThrottle;
  return function() {
    const args = arguments;
    const context = this;
    if (!inThrottle) {
      func.apply(context, args);
      inThrottle = true;
      setTimeout(() => (inThrottle = false), limit);
    }
  };
};

const useFetchUsers = createHook(() => {
  const [users, setUsers] = useState({});
  const [again, updater] = useState();

  async function fetchData() {
    const data = await fetch('https://randomuser.me/api/?results=10');
    console.log('[useFetch]: Fetched 10 random users');
    setUsers(data);
  }

  useEffect(() => {
    fetchData();
  }, [again]);

  const fetchAgain = useCallback(throttle(() => updater(s => ~s), 1000));

  return [users, fetchAgain];
});

const Hookletons = () => {
  useFetchUsers();
  return null;
};

let user;

const Gender = ({ idx }) => {
  const [data] = useFetchUsers();
  return data[idx].gender;
};

const Genders = () => {
  const [data, fetchAgain] = useFetchUsers();
  let user;
  return (
    <ul style={{ listStyle: 'none', borderStyle: 'solid' }}>
      {Array.from({ length: 10 }).map(
        (_, idx) => (
          (user = data[idx]),
          user ? (
            <il key={idx}>
              <span>{idx}</span>
              <Gender idx={idx} />
              <br />
            </il>
          ) : null
        )
      )}
      <il>
        <button onClick={fetchAgain}>fetch Again</button>
      </il>
    </ul>
  );
};

const Name = () => {};

const Location = () => {};

const Email = () => {};

export default () => (
  <>
    <Hookletons />
    <Genders />
  </>
);
