import { useState, useEffect } from 'react';
import createHookleton from '../component';
import createPredicateHook from '../component/predicateHookleton';

const useMi = () => {
  const tuple = useState(0);
  //return tuple.concat(Math.random());
  return tuple.concat(10);
};

const useAccelerator = createHookleton(useMi, 0);
const useAccelerator2 = createPredicateHook(useMi, 0);

useAccelerator.updater(1);
useAccelerator2.updater(3);

const Logo = ({ a, color }) => {
  //const [acc, setAcc] = useAccelerator(s => s > 5);
  const [acc, setAcc, rnd, setPredicate] = useAccelerator();

  useEffect(() => {
    console.log(setAcc(s => s+1))
  }, [])

  return (
    <>
      <p>Logo {acc}</p>
      <button onClick={() => setAcc(s => s + 1)}>+</button>
      <button onClick={() => setAcc(s => s)}>nothing</button>
    </>
  );
};

const Single = () => {
  useAccelerator2();
  return null;
};

const Logo2 = ({ a, color }) => {
  //const [acc, setAcc] = useAccelerator(s => s > 5);
  const [acc, setAcc, rnd, setPredicate] = useAccelerator2(s => s > 5);
  return (
    <>
      <p>Logo2 {acc}</p>
      {rnd}
      <button onClick={() => setAcc(s => s + 1)}>+</button>
      <button onClick={() => setPredicate(s => s < 5)}>set</button>
    </>
  );
};

export default () => (
  <table>
    <Single />
    <tbody>
      <tr>
        {Array.from({ length: 4 }).map((_, i) => (
          <td key={i}>
            <Logo />
          </td>
        ))}
        <td>
          <Logo2 />
        </td>
      </tr>
    </tbody>
  </table>
);
