import { useState, useEffect, useRef, useMemo } from 'react';
import { Hookleton } from './index';

export default function createPredicateHook(useHook, ...initialArgs) {
  const hook = new Predicate(useHook, initialArgs);
  const useFn = hook.useFn.bind(hook);
  useFn.get = hook._get.bind(hook);
  return useFn;
}

class Predicate extends Hookleton {
  constructor(useHook, initialArgs) {
    super(useHook, initialArgs);
  }

  useOnRenderRest([arg1]) {
    let predicate;
    const hasSet = useRef(false);
    useMemo(() => {
      if (Array.isArray(arg1)) {
        hasSet.current = true;
        predicate = arg1[0];
      } else {
        predicate = arg1;
      }
    }, []);

    const [, updater] = useState();

    useEffect(() => {
      this._saveUpdater(updater, predicate);
      return () => this._updaters.delete(updater); // on unmount
    }, []);

    return hasSet.current
      ? this.hookOut.concat({ setPredicate: this._setPredicate(updater) })
      : this.hookOut;
  }

  _saveUpdater(updater, predicate) {
    this._updaters.set(updater, withPredicate(updater, predicate));
  }

  _setPredicate(updater) {
    return predicate => {
      this._saveUpdater(updater, predicate);
      this._updaters.get(updater)(this.hookOut[0]);
    };
  }
}

// Add 'predicate' function to updater
function withPredicate(updater, p) {
  const predicate = normalize(p);
  return s => predicate(s) && updater(forceUpdate);
}

const forceUpdate = s => ~s;

function normalize(p) {
  switch (typeof p) {
    case 'undefined':
      return TRUEFN;
    case 'function':
      return p;
    case 'boolean':
      return () => p;
    default:
      console.warn("[Hookleton] predicate '" + p + "' is not boolean or function");
      return FALSEFN;
  }
}

const TRUEFN = () => true;
const FALSEFN = () => false;
