import { useState } from 'react';
import createHook from '../component';

// useExample is a useState but global
const useExample = createHook(useState);

const Increment = () => {
  const [, update] = useExample();
  const increment = () => update(s => s + 1);
  return <button onClick={increment}>+</button>;
};

const Decrement = () => {
  const [, update] = useExample();
  const decrement = () => update(s => s - 1);
  return <button onClick={decrement}>-</button>;
};

const Value = () => {
  const [value] = useExample(0);
  return <span>{value}</span>;
};

export default () => (
  <div>
    <Value />
    <Increment />
    <Decrement />
  </div>
);
