import { useEffect, useMemo, useRef, useState } from 'react';

export function createHookleton(useHook, ...initialArgs) {
  return createHookletonWithConfig({}, useHook, ...initialArgs);
}

export function createHookletonWithConfig(config = {}, useHook, ...initialArgs) {
  // config defaults
  const useSlave = config.useSlave || _useSlave;
  const useRenderSlave = config.useRenderSlave || _useRenderSlave;
  const unmountedSingleton = config.unmountedSingleton || unmountedSingletonWarn;

  // Singleton(master) state array shared with slaves
  let hookOut;
  const slaves = new Map();


  let setIsMaster = isMaster => {
    setIsMaster = () => {}; // dummify the setter for next calls
    isMaster.current = true; // the first call is always the 'master'
  };

  // Custom React Hook
  const useFn = initial => {
    // Initialize 'master' and 'slaves'
    const isMaster = useRef(false);
    useMemo(() => setIsMaster(isMaster), []); // only one `useFn` instance will be true, 'the master'

    if (isMaster.current) {
      // Update state, only 'master' can
      hookOut = useHook(...initialArgs); // state = hookOut[0] .. updater = hookOut[1]

      // Checked on first 'master' render
      useMemo(() => {
        if (!Array.isArray(hookOut)) {
          throw new Error("[Hookleton] provided 'useHook' must return array values");
        }
      }, []);

      // prevent Exception on 'master' unmount
      useEffect(() => () => (hookOut[1] = unmountedSingleton), []);

      // Render 'slaves'
      useRenderSlave({ slaves, hookOut });

      return hookOut;
    }

    return useSlave({ slaves, hookOut }, initial);
  };

  // Inject 'geState' and 'updater', and 'rest', to Hook function object for standalone use,
  // Ex: outside of a Component
  useFn.getState = () => hookOut[0];
  useFn.updater = s => hookOut[1](s);
  // Will return a opaque 'rest' array of Hook output, empty [] in the worst case
  useFn.rest = () => hookOut.slice(2, hookOut.length);

  return useFn;
}

// default 'useSlave'
const _useSlave = ({ slaves, hookOut }) => {
  // Get slave 'updater' for, later, force render it. Slave state is ignore.
  const [, updater] = useState();

  useEffect(() => {
    // a Map of key/value: updater/updater is useful for some kinds of hooks composed on top of `useFn`
    slaves.set(updater, updater);
    return () => slaves.delete(updater); // on unmount
  }, []);

  return hookOut;
};

// We don't try to be too much protective with 'slaves' container. Internal Map is
// exposed for advanced users that need performance. You will have to take care yourself.
// default 'useRenderSlave'
const _useRenderSlave = ({ slaves, hookOut }) => {
  useEffect(() => slaves.forEach(updater => updater(s => ~s)), [hookOut[0]]); // Force render slaves' when state has changed
};

// Prevent a Exception get throwed when someone is trying to update a unmounted 'master',
// behaviour can be changed with `unmountedSingleton` config setting
function unmountedSingletonWarn() {
  console.warn("[Hookleton] Singleton it's unmounted");
}

